wp_revslider_navigations.css:
{"arrows":".megatron.tparrows {
	cursor:pointer;
    background:transparent;
	width:44px;
	height:44px;
	position:absolute;
	display:block;
	z-index:100;
    border:1px solid rgba(255,255,255,0.5);
    -webkit-border-radius: 100%;
    -moz-border-radius: 100%;
    border-radius: 100%;
    -webkit-transition: all .3s;
  -moz-transition: all .3s;
  -ms-transition: all .3s;
  -o-transition: all .3s;
  transition: all .3s;


}
.megatron.tparrows:hover {
	background:#00d9d9;
    border-color:#00d9d9;
}
.megatron.tparrows:hover:before {
color: #fff;
}
.megatron.tparrows:before {
	font-family: \"revicons\";
	font-size:15px;
    color:rgba(255,255,255,0.5);
	display:block;
	line-height: 44px;
	text-align: center;
    -webkit-transition: all .3s;
  -moz-transition: all .3s;
  -ms-transition: all .3s;
  -o-transition: all .3s;
  transition: all .3s;
}
.megatron.tparrows.tp-leftarrow:before {
	content: \"\\e824\";
}
.megatron.tparrows.tp-rightarrow:before {
	content: \"\\e825\";
}

","bullets":".megatron.tp-bullets {
}
.megatron.tp-bullets:before {
	content:\" \";
	position:absolute;
	width:100%;
	height:100%;
	background:#transparent;
	padding:10px;
	margin-left:-10px;margin-top:-10px;
	box-sizing:content-box;
}
.megatron .tp-bullet {
  -webkit-border-radius: 100%;
  -moz-border-radius: 100%;
   border-radius: 100%;
	width:11px;
	height:11px;
	position:absolute;
	background:transparent;
	border:1px solid #fff;	
	cursor: pointer;
	box-sizing:content-box;
    -webkit-transition: all .3s;
  -moz-transition: all .3s;
  -ms-transition: all .3s;
  -o-transition: all .3s;
  transition: all .3s;
}
.megatron .tp-bullet:hover,
.megatron .tp-bullet.selected {
	background:#fff;
}
.megatron .tp-bullet-image {
}
.megatron .tp-bullet-title {
}
"}
